import React, { useState } from 'react';
import './HomeContent.module.css'
import { getDataSource, DataType} from './data';
import { Button, Divider, Popconfirm, Radio, Space, Switch, Table, Tag } from 'antd';
import { ColumnsType } from 'antd/es/table';
const { Column, ColumnGroup } = Table;


const rowSelection = {
    onChange: (selectedRowKeys: React.Key[], selectedRows: DataType[]) => {
      console.log(`selectedRowKeys: ${selectedRowKeys}`, 'selectedRows: ', selectedRows);
    },
  };

  export const columns:ColumnsType<DataType> = [
    {
        title: '渠道',
        dataIndex: 'ch_type',
        key: 'ch_type',
        width:70
    },
    {
        title: '应用',
        dataIndex: 'name',
        key: 'name',
        width:70,
        align:'center'


    },
    {
        title: '内容',
        dataIndex: 'content',
        key: 'content',
        align:'center'
    },
    {
        title: '日期',
        dataIndex: 'created_at',
        key: 'created_at',
        width:200,
        align:'center'
    },
    {
        title: 'AI',
        dataIndex: 'isAe',
        key: 'isAe',
        width:70,
        align:'center'
    },
    {
        title: '关于AE?',
        dataIndex: 'isAe',
        key: 'isAe',

        align:'center',
        render: (_, record) => (
          <div>
            <Button style={{backgroundColor:'#ee6736'}}>是</Button>
            <Button style={{marginLeft:10}}>否</Button>
          </div>
        ),

    },

];
function HomeContent() {  

    return (
        <div>
          <Table  rowSelection={{
            type: 'checkbox',
          }} 
          columns={columns} 
          dataSource={getDataSource()} 
          scroll={{ x: 0}} 
          summary={() => (
            <Table.Summary fixed={'top'}>
            </Table.Summary>
          )}
          sticky
          />
        </div>
    )
}

export default HomeContent;
