                    style={{ ...{ width: value.width }, ...{ marginBottom: 10, paddingRight: 10 } }}
                    color="secondary"
                    id={value.id}
                    label={<div style={{ fontSize: 13, float: 'left' }}>
                        {value.label}
                    </div>}
                    defaultValue=""
