    },
    render: (_, record) => (
      <div>
        <Button style={{backgroundColor:'#ee6736', color:'white'}}>是</Button>
        <Button style={{marginLeft:10}}>否</Button>
      </div>
    ),
  });

  export const columns:ColumnsType<DataType> = [
    {
        title: '渠道',
        dataIndex: 'ch_type',
        key: 'ch_type',
        width:70
    },
    {
        title: '应用',
        dataIndex: 'name',
        key: 'name',
        width:140,
        align:'center'


    },
    {
        title: '内容',
        dataIndex: 'content',
        key: 'content',
        align:'center'
    },
    {
        title: '日期',
