        align:'center'
    },
    {
        title: '关于AE?',
        dataIndex: 'isAe',
        key: 'isAe',
        width:180,
        align:'center',
        render: (_, record) => (
          <div>
            <Button style={{backgroundColor:'#ee6736', color:'white'}}>是</Button>
            <Button style={{marginLeft:10}}>否</Button>
          </div>
        ),
        filterIcon: (filtered: boolean) => (
          <SearchOutlined style={{ color: filtered ? '#1677ff' : undefined }} />
        ),

    },

];






function HomeContent() {  

    return (
        <div>
          <Table  rowSelection={{
            type: 'checkbox',
          }} 
          columns={columns} 
          dataSource={getDataSource()} 
          scroll={{ x: 0}} 
          summary={() => (
            <Table.Summary fixed={'top'}>
            </Table.Summary>
          )}
          sticky
          />
        </div>
    )
}

export default HomeContent;
