import React, { useState } from 'react';
import styles from './HomeContent.module.css'
import { getDataSource, DataType } from './data';
import { Button, Divider, Dropdown, MenuProps, Popconfirm, Radio, Space, Switch, Table, Tag, Tooltip } from 'antd';
import { ColumnType, ColumnsType } from 'antd/es/table';
import { SearchOutlined, VerticalAlignBottomOutlined, VerticalAlignTopOutlined } from '@ant-design/icons';
import { text } from 'stream/consumers';
const { Column, ColumnGroup } = Table;

let isFilter = true;

const rowSelection = {
  onChange: (selectedRowKeys: React.Key[], selectedRows: DataType[]) => {
    console.log(`selectedRowKeys: ${selectedRowKeys}`, 'selectedRows: ', selectedRows);
  },
};


const getColumnSearchProps = (): ColumnType<DataType> => ({
  filterDropdown: ({ setSelectedKeys, selectedKeys, confirm, clearFilters, close }) => (
    <></>
  ),

  filterIcon: (filtered: boolean) => {
    return (
      <div
        className={styles.headerFilterBox} >
        <Button onClick={() => {
          console.log("click-----icon")
          isFilter = !isFilter;
        }} className={styles.headerFilterIcon} type='link' icon={isFilter ? <VerticalAlignTopOutlined /> : <VerticalAlignBottomOutlined />}></Button>

      </div>
    )
  },

  render: (_, record) => (
    <div onMouseEnter={(e) => e.stopPropagation()}>
      <Button style={{ backgroundColor: '#ee6736', color: 'white' }}>是</Button>
      <Button style={{ marginLeft: 10 }}>否</Button>
    </div>
  ),
});

const columns: ColumnsType<DataType> = [
  {
    title: '渠道',
    dataIndex: 'ch_type',
    key: 'ch_type',
    width: 70
  },
  {
    title: '应用',
    dataIndex: 'name',
    key: 'name',
    width: 140,
    align: 'center'


  },
  {
    title: '内容',
    dataIndex: 'content',
    key: 'content',
    align: 'center',
    render: (_, record) => (
      <Tooltip placement="top" title={record.content} arrow={false}>
      <div>
        {record.content}
      </div>
      </Tooltip>

    ),

  },
  {
    title: '日期',
    dataIndex: 'created_at',
    key: 'created_at',
    width: 200,
    align: 'center'
  },
  {
    title: 'AI',
    dataIndex: 'isAe',
    key: 'isAe',
    width: 70,
    align: 'center'
  },
  {
    title: '关于AE?',
    dataIndex: 'isAe',
    key: 'isAe',
    width: 200,
    align: 'center',
    ...getColumnSearchProps()

  },

];


const items: MenuProps['items'] = [
  {
    key: '1',
    label: (
      <div>AE</div>
    ),
  },
  {
    key: '2',
    label: (
      <div>非AE</div>
    ),
  },
  {
    key: '3',
    label: (
      <div>全部</div>
    ),
  },
];


function HomeContent() {

  return (
    <div className={styles.contentBox}>
      <div className={styles.tableBox}>
        <Table rowSelection={{
          type: 'checkbox',
        }}
          columns={columns}
          dataSource={getDataSource()}
          scroll={{ x: 0 }}
          sticky

        />
      </div>
      <Dropdown menu={{ items }} placement="bottom" arrow>
      <Tooltip open={false} placement="top" title={'点击切换状态'} arrow={false}>

        <div className={styles.pendingBox}>待处理</div>
      </Tooltip>
      </Dropdown>


    </div>
  )
}

export default HomeContent;
